import fetch from 'isomorphic-unfetch';
import Header from '../components/header';
import Nav from '../components/nav';

function Index({ blogs }) {
  return <div>
            <Header title="test" />
            <Nav />
            <div className="container">
              <div className="row">
                <div className="col-md-5">
                    <div className="primary-item">
                      <a href={"/blog?title=" + blogs[0].filename}>
                        <h2>{blogs[0].title}</h2>
                        <img src={"/public/img/blogs/" + blogs[0].image} alt={blogs[0].title} />
                      </a>
                    </div>
                </div>
                <div className="col-md-7 sub-primary-item">
                  <div className="col-md-6">
                      <img src={"/public/img/blogs/" + blogs[1].image} width="100" alt={blogs[1].title}/>
                      <h3>{blogs[1].title}</h3>
                      <img src={"/public/img/blogs/" + blogs[2].image} width="100" alt={blogs[2].title}/>
                      <h3>{blogs[2].title}</h3>
                  </div>
                  <div className="col-md-6">
                      <img src="https://cdn.cnn.com/cnnnext/dam/assets/191226161551-02-james-seymour-mansion-medium-tease.jpg" width="100" />
                      <h3>This 10-bedroom mansion in New York was offered for only $50,000, with one catch</h3>
                      <img src="https://cdn.cnn.com/cnnnext/dam/assets/191226175251-peloton-husband-medium-tease.jpg" width="100" />
                      <h3>'Peloton husband' gifts real girlfriend wth a Peloton bike for Christmas</h3>
                  </div>
              </div>
            </div>
          </div>
      </div>
}

Index.getInitialProps = async ctx => {
  const fs = require('fs');
  let blogs = [];

  let files = fs.readdirSync("./data", { withFileTypes: true });
  const filesNames = files
    .filter(files => !files.isDirectory())
    .map(files => files.name);

  filesNames.forEach(file => {
    let blog = {};
    let data = fs.readFileSync("./data/" + file).toString();
    data = data.split("|");
    blog.title = data[0];
    blog.filename = data[1];
    blog.content = data[2];
    blog.image = data[3];
    blog.author = data[4];
    blog.date = data[5];
    blogs.push(blog);
  });

  blogs = blogs.sort((a, b) => new Date(b.date) - new Date(a.date));

  return { blogs: blogs }
}

export default Index;
